mapper.select:
SELECT
    tam.*,
    tamt.template_category,
    tamt.template_type
FROM
    t_data_app_message tam
        LEFT JOIN t_data_app_message_template tamt ON tam.message_template_id = tamt.id
WHERE
    tam.dr = 0 and tamt.dr = 0

    AND tam.channel = #{inputDto.channel}


    AND tamt.template_type = #{inputDto.templateType}


    AND tamt.template_category = #{inputDto.templateCategory}


    AND tam.dispose = #{inputDto.dispose}


    AND tam.content like CONCAT(CONCAT('%', #{inputDto.informContent}), '%')


    AND tam.update_by IN

        #{item}



    and tam.update_time >= #{inputDto.updateStartTime}


    and tam.update_time <= #{inputDto.updateEndTime}

SELECT
tam.*,
tamt.template_category,
tamt.template_type
FROM
t_app_message tam
LEFT JOIN t_app_message_template tamt ON tam.message_template_id = tamt.id
WHERE
tam.dr = 0 and tamt.dr = 0
and tam.receiver = #{inputDto.receiver}

    AND tam.channel = #{inputDto.channel}


    AND tamt.template_type = #{inputDto.templateType}


    AND tamt.template_type IN

        #{item}



    AND tamt.template_category = #{inputDto.templateCategory}


    AND tam.dispose = #{inputDto.dispose}


    AND tam.readed = #{inputDto.readed}


    AND tam.content like CONCAT(CONCAT('%', #{request.informContent}), '%')


    and tam.update_time >= #{inputDto.updateStartTime}


    and tam.update_time <= #{inputDto.updateEndTime}

order by tam.update_time desc

WITH RankedMessages AS ( SELECT tmt.template_category, am.inform_content, am.update_time, COUNT(am.id) OVER (PARTITION BY tmt.template_category) AS count, ROW_NUMBER() OVER (PARTITION BY tmt.template_category ORDER BY am.update_time DESC) AS rn FROM t_app_message am JOIN t_app_message_template tmt ON am.message_template_id = tmt.id
AND am.receiver = #{inputDto.receiver}
AND am.readed = #{inputDto.readed}
AND am.channel = #{inputDto.channel}
) SELECT template_category, inform_content, update_time, count FROM RankedMessages WHERE rn = 1 ORDER BY template_category;
