mapper.select:
SELECT slor.label_option_id
FROM t_data_shop_label_option_relation slor
WHERE slor.shop_id = #{shopId}

SELECT slor.shop_id
FROM t_data_shop_label_option_relation slor
WHERE slor.label_option_id = #{labelOptionId}

SELECT count(0) AS count, t.label_option_id as id FROM ( SELECT slor.label_option_id, slor.shop_id FROM t_data_shop_label_option_relation slor LEFT JOIN t_data_shop s ON s.id = slor.shop_id WHERE slor.label_option_id IN
#{item}
) t GROUP BY t.label_option_id
