mapper.select:
select r.id
from t_iam_role r
where r.parent_id = #{inputDto.id}

    AND r.status = #{inputDto.status}

select r.id
from t_iam_role r
where r.type = #{type}

select r.*
from t_iam_role r
where r.parent_id = #{inputDto.id}

    AND r.status = #{inputDto.status}

select r.*
from t_iam_role r


        AND r.type = #{inputDto.type}


        AND r.status = #{inputDto.status}


        AND LOWER(r.code) LIKE LOWER(CONCAT('%', #{inputDto.code}, '%'))


        AND r.name like CONCAT('%', #{inputDto.name}, '%')


        AND r.create_by IN

            #{item}



        AND r.update_by IN

            #{item}



        AND r.create_time > #{inputDto.createStartTime}


        AND r.create_time  <   #{inputDto.createEndTime}


        AND r.update_time > #{inputDto.updateStartTime}


        AND r.update_time  <   #{inputDto.updateEndTime}


ORDER BY r.sort DESC, r.create_time DESC
