mapper.select:
select u.*
from t_iam_user u

    LEFT JOIN t_iam_user_type_relation utr on utr.user_id = u.id



        AND u.status = #{inputDto.status}


        AND utr.user_type = #{inputDto.userType}


        AND u.id > #{inputDto.offset}


ORDER BY u.id ASC
LIMIT #{inputDto.size}

SELECT u.* FROM t_iam_user u


        AND u.id IN

            #{item}



        AND u.username like CONCAT('%', #{inputDto.username}, '%')


        AND u.name like CONCAT('%', #{inputDto.name}, '%')


        AND LOWER(u.code) like LOWER(CONCAT('%', #{inputDto.code}, '%'))


        AND u.status = #{inputDto.status}


        AND u.phone like CONCAT('%', #{inputDto.phone}, '%')


        AND u.gender = #{inputDto.gender}


        AND u.update_time > #{inputDto.createStartTime}


        AND u.update_time  <   #{inputDto.createEndTime}


        AND u.update_time > #{inputDto.updateStartTime}


        AND u.update_time  <   #{inputDto.updateEndTime}


ORDER BY u.create_time DESC

SELECT u.* FROM t_iam_user u
LEFT JOIN t_iam_user_type_relation utr on utr.user_id = u.id

    utr.user_type = 'COMPANY'

        AND u.id IN

            #{item}



        AND u.username like CONCAT('%', #{inputDto.username}, '%')


        AND LOWER(u.code) like LOWER(CONCAT('%', #{inputDto.code}, '%'))


        AND u.phone like CONCAT('%', #{inputDto.phone}, '%')


        AND u.name like CONCAT('%', #{inputDto.name}, '%')


        AND u.status = #{inputDto.status}


        AND u.update_time > #{inputDto.createStartTime}


        AND u.update_time  <   #{inputDto.createEndTime}


        AND u.update_time > #{inputDto.updateStartTime}


        AND u.update_time  <   #{inputDto.updateEndTime}


ORDER BY u.create_time DESC

SELECT u.* FROM t_iam_user u
LEFT JOIN t_iam_user_type_relation utr on utr.user_id = u.id

    utr.user_type in ('SHOP','FRANCHISEE')
    AND u.id NOT IN (SELECT user_id FROM t_iam_user_type_relation WHERE user_type = 'COMPANY')

        AND u.id IN

            #{item}



        AND u.username like CONCAT('%', #{inputDto.username}, '%')


        AND LOWER(u.code) like LOWER(CONCAT('%', #{inputDto.code}, '%'))


        AND u.phone like CONCAT('%', #{inputDto.phone}, '%')


        AND u.name like CONCAT('%', #{inputDto.name}, '%')


        AND u.status = #{inputDto.status}


        AND u.update_time > #{inputDto.createStartTime}


        AND u.update_time  <   #{inputDto.createEndTime}


        AND u.update_time > #{inputDto.updateStartTime}


        AND u.update_time  <   #{inputDto.updateEndTime}


ORDER BY u.create_time DESC

SELECT u.* FROM t_iam_user u
LEFT JOIN t_iam_user_type_relation utr on utr.user_id = u.id

    utr.user_type in ('SUPPLIER')
    AND u.id NOT IN (SELECT user_id FROM t_iam_user_type_relation WHERE user_type IN
    ('COMPANY','SHOP','FRANCHISEE'))

        AND u.id IN

            #{item}



        AND u.username like CONCAT('%', #{inputDto.username}, '%')


        AND LOWER(u.code) like LOWER(CONCAT('%', #{inputDto.code}, '%'))


        AND u.phone like CONCAT('%', #{inputDto.phone}, '%')


        AND u.name like CONCAT('%', #{inputDto.name}, '%')


        AND u.status = #{inputDto.status}


        AND u.update_time > #{inputDto.createStartTime}


        AND u.update_time  <   #{inputDto.createEndTime}


        AND u.update_time > #{inputDto.updateStartTime}


        AND u.update_time  <   #{inputDto.updateEndTime}


GROUP BY u.id
ORDER BY u.create_time DESC

